sp_widget.client_script:
function($scope, $location) {
  /* widget controller */
	var c = this;
	var url = "https://www.citizensinformation.ie/en/employment/return_to_work/returning_to_work_and_covid19.html";


	$scope.iframeUrl = url;
}

sp_widget.link:
function(scope, elem, attrs) {
	var panelHeight;

	$(document).ready(function() {
		resizeWindow();
	});

	$(window).resize(function() {
		resizeWindow();
	});

	function resizeWindow() {
		var panelHeight = ($(document).height()- 60) + 'px';
		$(elem).find('#pwd_reset_container').css('height', panelHeight);
	}
}
